function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body"),c=document.querySelector(".nav__btns");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"),c.classList.remove("active")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"),c.classList.add("active"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"),c.classList.remove("active"))}))}function submenu(){const e=document.querySelector(".submenu"),s=document.querySelector(".with__submenu"),t=document.querySelector(".submenu__arrow");s.addEventListener("click",(()=>{e.classList.toggle("active"),s.classList.toggle("active"),t.classList.toggle("active")}))}burgerMenu(),submenu();